ValidationRule.errorConditionFormula:
AND(
ISPICKVAL( Status , 'Activated') , NOT(ISCHANGED(Status )), NOT( ISCHANGED( PaymentTerms__c )),NOT( ISCHANGED( OwnerId )),NOT( ISCHANGED( PaymentType__c )), NOT( ISCHANGED( CreditMemoOrderAmount__c )),NOT( ISCHANGED( DiscountAmount__c )),NOT( ISCHANGED( TaskCreated__c ))
)
